      if (!registration) {
        return res.status(404).json({ error: "Registration not found" });
      }

      res.json(registration);
    } catch (error) {
      console.error("Error fetching bootcamp registration:", error);
      res.status(500).json({ error: "Failed to fetch bootcamp registration" });
    }
