                      )}
                    />
                  </motion.div>

                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.9, duration: 0.4 }}
                  >
                    <FormField
                      control={form.control}
                      name="paymentProof"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="flex items-center gap-2">
                            <motion.div
                              animate={{ y: [0, -3, 0] }}
                              transition={{ duration: 1.5, repeat: Infinity }}
                            >
                              <Upload className="w-4 h-4" />
                            </motion.div>
                            Payment Screenshot *
                          </FormLabel>
                          <FormControl>
                            <motion.div 
                              className={`border-2 border-dashed rounded-lg p-4 md:p-6 text-center transition-all duration-300 cursor-pointer ${uploadedFile ? 'border-green-500 bg-green-50' : 'border-gray-300 hover:border-blue-500 hover:bg-blue-50/30'}`} 
                              data-testid="upload-payment-area"
                              whileHover={{ scale: 1.01 }}
                              whileTap={{ scale: 0.99 }}
                            >
                              <input
                                type="file"
                                accept="image/jpeg,image/png,image/gif,application/pdf"
                                onChange={handleFileChange}
                                className="hidden"
                                id="payment-upload"
                                data-testid="input-payment-upload"
                              />
                              <label htmlFor="payment-upload" className="cursor-pointer block">
                                <AnimatePresence mode="wait">
                                  {uploadedFile ? (
                                    <motion.div 
                                      key="uploaded"
                                      initial={{ opacity: 0, scale: 0.8 }}
                                      animate={{ opacity: 1, scale: 1 }}
                                      exit={{ opacity: 0, scale: 0.8 }}
                                      className="text-sm"
                                    >
                                      <motion.div
                                        initial={{ scale: 0 }}
                                        animate={{ scale: 1 }}
                                        transition={{ type: "spring", stiffness: 200 }}
                                      >
                                        <Check className="w-8 h-8 text-green-500 mx-auto mb-2" />
                                      </motion.div>
                                      <p className="font-semibold text-green-600 mb-1">Payment Proof Uploaded</p>
                                      <p className="text-gray-600">{uploadedFile.name}</p>
                                      <p className="text-xs text-gray-500 mt-1">
                                        {(uploadedFile.size / 1024).toFixed(2)} KB
                                      </p>
                                    </motion.div>
                                  ) : (
                                    <motion.div 
                                      key="upload"
                                      initial={{ opacity: 0 }}
                                      animate={{ opacity: 1 }}
                                      exit={{ opacity: 0 }}
                                      className="text-sm"
                                    >
                                      <motion.div
                                        animate={{ y: [0, -5, 0] }}
